body {
  justify-content: center;
  align-items: center;
  font-family: 'Montserrat Alternates', sans-serif;
  margin: 0;
  min-width: 100vh;
  min-height: 100vh;
  background-color: #0b1706;
  color: white;
}

h1 {
  margin-top: 50px;
  text-align: center;
  font-weight: 800;
  font-size: 52px;
}

p {
  font-family: 'Telex', sans-serif;
  text-align: center;
  font-weight: 400;
  font-size: 18px;
  padding-bottom: 10px;
}

.instructions {
  margin-right: 40px;
  margin-left: 40px;

}

.controls {
  display: flex;
  justify-content: center;
}

button {
  padding: 12px 45px;
  font-size: 20px;
  border-radius: 11px;
  background-color: #419F35;
  color: rgb(251, 228, 228);
  text-transform: uppercase;
}

.game-container {
  display: grid;
  justify-content: center;
  grid-gap: 17px;
  grid-template-columns: repeat(6, 120px); /* 6 cards per row, each 120px wide */
  grid-template-rows: repeat(2, calc(120px / 2 * 3));
}

.card {
  height: calc(120px / 2 * 3);
  width: 120px;
  border-radius: 11px;
  background-color: white;
  position: relative;
  transform-style: preserve-3d;
  transition: transform 0.5s, box-shadow 0.2s;
  box-shadow: 0px 4px 8px rgba(0,0,0,0.2);
  perspective: 1000px; /* For 3D effect */
}

.front-image {
  width: 70px;
  height: 70px;
}

.card.flipped {
  transform: rotateY(180deg);
  box-shadow: 0px 4px 8px rgba(0,0,0,0.5);
}

.front, 
.back {
  backface-visibility: hidden;
  position: absolute;
  border-radius: 11px;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
}

.card .front {
  display: flex;
  justify-content: center;
  align-items: center;
  transform: rotateY(180deg);
}

