.matched {
  animation: matchedAnimation 0.8s ease-out;
}

@keyframes matchedAnimation {
  0% { transform: scale(0.95); }
  50% { transform: scale(1.1); }
  100% { transform: scale(1); }
}

.mismatch {
  animation: shake 0.5s ease-in-out;
}

@keyframes shake {
  0%, 100% { transform: translateX(0); }
  25%, 75% { transform: translateX(-5px); }
  50% { transform: translateX(5px); }
}

.snowflake {
  position: absolute;
  top: -100px; 
  width: 10px;
  height: 10px;
  background-color: white;
  border-radius: 50%;
  opacity: 0.8;
  filter: blur(4px);
  animation: falling 12s linear infinite, colorChange 5s ease-in-out infinite;
}


@keyframes falling {
  to {
    transform: translateY(100vh); /* Moves the snowflake from top to bottom of the viewport */
  }
}
@keyframes colorChange {
  0% { background-color: #FFFFFF; } /* White */
  20% { background-color: #AEC6CF; } /* Light Blue */
  40% { background-color: #FFB347; } /* Orange */
  60% { background-color: #77DD77; } /* Light Green */
  80% { background-color: #FF6961; } /* Light Red */
  100% { background-color: #FFFFFF; } /* White again */
}


.snowflake:nth-child(1) {
  left: 92%;
  animation-delay: 7s, 3s;
}

.snowflake:nth-child(2) {
  left: 18%;
  animation-delay: 1s, 3s;
}

.snowflake:nth-child(3) {
  left: 71%;
  animation-delay: 1.5s, 7s;
}

.snowflake:nth-child(4) {
  left: 42%;
  animation-delay: 2.5s, 2s;
}

.snowflake:nth-child(5) {
  left: 2%;
  animation-delay: 4s, 6s;
}

.snowflake:nth-child(6) {
  left: 63%;
  animation-delay: 5s, 1s;
}

.snowflake:nth-child(7) {
  left: 34%;
  animation-delay: 3s, 5s;
}

.snowflake:nth-child(8) {
  left: 85%;
  animation-delay: 6s, 4s;
}

.snowflake:nth-child(9) {
  left: 5%;
  animation-delay: 10s, 6s;
}

.snowflake:nth-child(10) {
  left: 51%;
  animation-delay: 8s, 2s;
}